
var resemble = require('node-resemble')
var options = {
    expectedFile: __dirname + '/snapshots/sample_before.png',
    actualFile: __dirname + '/snapshots/sample_after.png'
}


var x = resemble(options.actualFile)
    .compareTo(options.expectedFile)
    .onComplete(function(data) {
        console.log(data)
    })


