var Browser = require('zombie')
var zombie = new Browser()

var zombiecss = require('../src/zombiecss')

var beforeUrl = 'file:///Users/prestl02/Projects/zombiecss/sample/before.html'
var afterUrl = 'file:///Users/prestl02/Projects/zombiecss/sample/after.html'

zombie
    .visit(beforeUrl)
    .then(function() {
        zombiecss.screenshot(zombie, {
            baseDir: './snapshots/',
            actualFile: 'sample_before',
            selector: '#sample'
        })

        zombie.wait()
            .then(function() {
                zombie.visit(afterUrl)
                    .then(function() {
                        zombiecss.screenshot(zombie, {
                            baseDir: './snapshots',
                            actualFile: 'sample_after',
                            selector: '#sample'
                        })
                    })
            })
    })
